package test

import (
	"encoding/json"
	"recipeScraper/objects"
	"testing"
)

func FitMenCookMultiRecipe(t *testing.T, recipes []objects.Recipe) {
	resJson, _ := json.Marshal(recipes)

	if res := string(resJson); res != multiRecipeJson {
		t.Errorf("\nGot:\n%+v\n\nExpected:\n%+v", res, multiRecipeJson)
	}
}

func FitMenCookSingleRecipe(t *testing.T, recipes []objects.Recipe) {
	resJson, _ := json.Marshal(recipes)

	if res := string(resJson); res != singleRecipeJson {
		t.Errorf("\nGot:\n%+v\n\nExpected:\n%+v", res, singleRecipeJson)
	}
}

