package test

import (
	"encoding/json"
	"recipeScraper/objects"
	"testing"
)

func SkinnyTasteSimpleIngredients(t *testing.T, recipes []objects.Recipe) {
	resJson, _ := json.Marshal(recipes)

	if res := string(resJson); res != simpleIngredientsJson {
		t.Errorf("\nGot:\n%+v\n\nExpected:\n%+v", res, simpleIngredientsJson)
	}
}

func SkinnyTasteMultiIngredients(t *testing.T, recipes []objects.Recipe) {
	resJson, _ := json.Marshal(recipes)

	if res := string(resJson); res != multiIngredientsJson {
		t.Errorf("\nGot:\n%+v\n\nExpected:\n%+v", res, multiIngredientsJson)
	}
}

